mapper.select:
select r.rn_num ,u.u_id ,r.s_num ,r.rn_count ,r.rn_date ,r.rn_regdate ,r.rn_content ,r.rn_name ,s.s_name from reservation r join user u on r.u_id = u.u_id join store s on r.s_num = s.s_num
where s.s_num = #{_parameter}
order by r.rn_regdate desc

mapper.insert:

	insert into reservation(
		u_id    , s_num, rn_count,
		rn_date, rn_regdate, rn_content, rn_name
	) values (
		#{u_id} , #{s_num}, #{rn_count}, 
		#{rn_date}, now(), #{rn_content}, #{rn_name} 
	)
