mapper.insert:
insert into store(
	s_name  , c_num    , s_tel   , s_address, s_content,
	s_snsurl, s_hashtag, s_hours
) values (
	#{s_name}, #{c_num}   , #{s_tel}    , #{s_address}, #{s_content},
	#{s_snsurl}, #{s_hashtag}, #{s_hours}
)

mapper.select:
select s_num , s_name, c_category, s_tel , s_address, s_content, s_snsurl , s_hashtag, s_hours from store s join category c on s.c_num = c.c_num where s_num > 0
and c.c_num = #{category}
order by s_num desc limit #{pageStart}, #{perPageNum}
